		private Dictionary<QColor, Color> colorsLight = new Dictionary<QColor, Color>()
		{
			{ QColor.BackgroundDark, new Color(0.88f, 0.88f, 0.88f) },
			{ QColor.Background , new Color(0.761f, 0.761f, 0.761f) },
